using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace Example
{
    class Program
    {
        static void Main(string[] args)
        {
            String t;
            double x;
            double y1, y2, y3;
            t = Console.ReadLine();
            x = double.Parse(t);
            y1 = x * Math.PI;
            y2 = y1 / 180;
            y3 = Math.Tan(y2);
            Console.WriteLine("{0:F4}", y3);
        }
    }
}
